import React, { useState, useEffect } from 'react';
import axios from 'axios';

const CursorTracker = () => {
  const [cursorActiveTime, setCursorActiveTime] = useState(0);

  useEffect(() => {
    const handleMouseMove = () => {
      setCursorActiveTime((prevTime) => prevTime + 1);
    };

    window.addEventListener('mousemove', handleMouseMove);

    const sendCursorActivityToServer = () => {
      axios.post('http://localhost:5000/', { time: cursorActiveTime });
    };

    const activityInterval = setInterval(sendCursorActivityToServer, 60000);

    return () => {
      window.removeEventListener('mousemove', handleMouseMove);
      clearInterval(activityInterval);
    };
  }, [cursorActiveTime]);

  return (
    <div>
      <p>Time with cursor active: {cursorActiveTime} seconds</p>
    </div>
  );
};

export default CursorTracker;
